import { useEffect, useState } from "react";
import "./style.css";
import { Link } from "react-router-dom";

function Navbar() {
  const [isSticky, setIsSticky] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.scrollY > 0);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    // Burada arama işlemini gerçekleştirme kodlarını ekleyebilirsiniz
    console.log("Arama gönderildi:", searchQuery);
    setSearchQuery("");
  };

  return (
    <div className={`nav ${isSticky ? "sticky" : ""}`}>
      <div className="nav_container">
        <nav>
          <div className="nav_toggle" onClick={toggleDropdown}>
            <span className={`nav_toggle_icon ${isDropdownOpen ? "open" : ""}`}></span>
          </div>
          <ul className={`nav_menu ${isDropdownOpen ? "open" : ""}`}>
            <li>
              <Link to="/homes">Home</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
            <li>
              <Link to="/contact">Contact</Link>
            </li>
          </ul>
        </nav>
        <div className="search_bar">
            <form onSubmit={handleSearchSubmit}>
              <input
                type="text"
                placeholder="Search..."
                value={searchQuery}
                onChange={handleSearchChange}
              />
              <button type="submit">Search</button>
            </form>
          </div>
      </div>
    </div>
  );
}

export default Navbar;
